use function \Onewayhi\Database\Local\Query\fetch;
use function \Onewayhi\Database\Local\Query\fetch_all;

$array = [];

$id_user = 1;

$head = fetch(
	'SELECT * ' .
	'FROM network_users ' .
	'WHERE id = :id',
	['id' => $id_user]
);

$array['username'] = $head->username;
$array['account']  = $head->account_type;

children($id_user, $array);

/* Manual version
 *
 **
if (count(get_child($id_user)))
{
	foreach (get_child($id_user) as $child)
	{
		$array['children']['username'] = $child->username;
		$array['children']['account']  = $child->account_type;

		if (count(get_child($child->id)))
		{
			foreach (get_child($child->id) as $child2)
			{
				$array['children']['children']['username'] = $child2->username;
				$array['children']['children']['account']  = $child2->account_type;

				if (count(get_child($child2->id)))
				{
					foreach (get_child($child2->id) as $child3)
					{
						$array['children']['children']['children']['username'] = $child3->username;
						$array['children']['children']['children']['account']  = $child3->account_type;
					}

					//...
				}
			}
		}
	}
