// recursive version
function children($parent, &$array)
{
	if (count(get_child($parent)))
	{
		foreach (get_child($parent) as $child)
		{
			$array['children'][] = [
				'username' => $child->username,
				'account'  => $child->account_type
			];

			children($child->id, $array['children']);
		}
	}
}

function get_child($id)
{
	$sth = fetch_all(
		'SELECT * ' .
		'FROM network_users as u ' .
		'INNER JOIN network_binary as b ' .
		'ON u.id = b.id ' .
		'WHERE u.block = :block ' .
		'AND b.upline_id = :upline_id',
		[
			'upline_id' => $id,
			'block'     => 0
		]
	);

	return $sth;
}